def abbreviation(a, b):
    no_of_equals = 0
    index_of_a = 0
    index_of_b = 0
    while index_of_b<len(b) and index_of_a<len(a):
        if a[index_of_a] == b[index_of_b] or a[index_of_a].upper() == b[index_of_b]:
            index_of_a+=1
            index_of_b+=1
            no_of_equals+=1
        else:
            index_of_a+=1

    print(no_of_equals, len(a), len(b), index_of_a, index_of_b)
    if no_of_equals==len(b):
        for i in range(index_of_a,len(a)):
            if a[i].isupper():
                return False
        return True
    
    return False

